func checkVerifiedV1Cred(t *testing.T, cred *verifier.VerifiedCredential) {
	if cred.CredentialVersion != 1 ||
		cred.IssuerPkId != testIssuerPkId ||
		cred.Attributes["testType"] != "PCR" ||
		cred.Attributes["sampleTime"] != "1610000000" ||
		cred.Attributes["firstNameInitial"] != "A" ||
		cred.Attributes["birthDay"] != "13" {
		t.Fatal("Unexpected credential values for v1 credential")
	}
}

func buildCredentialsAttributes(credentialAmount int) []map[string]string {
	cas := make([]map[string]string, 0, credentialAmount)

	for i := 0; i < credentialAmount; i++ {
		validFrom := time.Now().Round(time.Hour).AddDate(0, 0, i).UTC().Unix()

		ca := map[string]string{
			"isSpecimen":       "0",
			"isPaperProof":     "0",
			"validFrom":        strconv.FormatInt(validFrom, 10),
			"validForHours":    "24",
			"firstNameInitial": "A",
			"lastNameInitial":  "R",
			"birthDay":         "20",
			"birthMonth":       "10",
		}

		cas = append(cas, ca)
	}

	return cas
}

func createIHV(t TB) (*issuer.Issuer, *holder.Holder, *big.Int, *verifier.Verifier) {
	iss := createIssuer(t)
	h, holderSk := createHolder()
	v := createVerifier()

	return iss, h, holderSk, v
}

func createIssuer(t TB) *issuer.Issuer {
	ls, err := localsigner.NewFromString(testIssuerPkId, testIssuerPkXML, testIssuerSkXML)
	if err != nil {
		t.Fatal("Could not create signer:", err.Error())
	}

	return issuer.New(ls)
}

func createHolder() (*holder.Holder, *big.Int) {
	holderSk := holder.GenerateSk()
	return holder.New(holderFindIssuerPk), holderSk
}

func createVerifier() *verifier.Verifier {
	return verifier.New(holderFindIssuerPk)
}

func holderFindIssuerPk(issuerPkId string) (*gabi.PublicKey, error) {
	issuerPks := map[string]*gabi.PublicKey{testIssuerPkId: testIssuerPk}
	issuerPk, ok := issuerPks[issuerPkId]
	if !ok {
		return nil, errors.Errorf("Could not find public key id chosen by issuer")
	}

	return issuerPk, nil
}

